import React from "react";

export default function MessageBubble({ msg }) {
  const isOutgoing = msg.direction === "outbound";
  return (
    <div className={`flex ${isOutgoing ? "justify-end" : "justify-start"} mb-2`}>
      <div className={`max-w-xs p-2 rounded-lg ${isOutgoing ? "bg-green-200" : "bg-white"} shadow`}>
        <div>{msg.message}</div>
        <div className="text-xs text-gray-500 text-right">
          {new Date(msg.timestamp).toLocaleTimeString()} â€¢ {msg.status}
        </div>
      </div>
    </div>
  );
}
